  @Override
  protected void onCreate(Bundle savedInstanceState) {
    super.onCreate(savedInstanceState);
    setContentView(R.layout.activity_message);

    // 初始化视图
    messageList = findViewById(R.id.message_list);

    // 加载留言数据
