class DatabaseHelper(private val context: Context) : SQLiteOpenHelper(context, "menu.db", null, 1) {
    override fun onCreate(db: SQLiteDatabase) {
        db.execSQL("CREATE TABLE menu (id INTEGER PRIMARY KEY, title TEXT, price REAL, content TEXT)")
    }

    override fun onUpgrade(db: SQLiteDatabase, oldVersion: Int, newVersion: Int) {
        db.execSQL("DROP TABLE IF EXISTS menu")
        onCreate(db)
    }
}

