// NetworkHelper.kt
import android.content.Context
import android.util.Log
import okhttp3.OkHttpClient
import okhttp3.Request
import okhttp3.Response

class NetworkHelper(private val context: Context) {
    fun uploadImage(imageUri: Uri): String {
        val client = OkHttpClient()
        val request = Request.Builder()
            .url("https://example.com/upload")
            .post(RequestBody.create(MediaType.get("image/jpeg"), Files.readBytes(imageUri)))
            .build()
        val response = client.newCall(request).execute()
        if (response.isSuccessful) {
            return response.body()!!.string()
        } else {
            Log.e("NetworkHelper", "Failed to upload image")
            return ""
        }
    }

    fun saveMenu(title: String, price: Double, content: String): String {
        val client = OkHttpClient()
        val request = Request.Builder()
            .url("https://example.com/save")
            .post(RequestBody.create(MediaType.get("application/json"), "{" + "title":"$title","price":$price,"content":"$content" + "}"))
            .build()
        val response = client.newCall(request).execute()
        if (response.isSuccessful) {
            return response.body()!!.string()
        } else {
            Log.e("NetworkHelper", "Failed to save menu")
            return ""
        }
    }
}

// Preferences.kt
import android.content.Context
import android.content.SharedPreferences

class Preferences(private val context: Context) {
    fun getBoolean(key: String, defaultValue: Boolean): Boolean {
        val preferences = context.getSharedPreferences("preferences", Context.MODE_PRIVATE)
        return preferences.getBoolean(key, defaultValue)
    }

    fun setBoolean(key: String, value: Boolean) {
        val preferences = context.getSharedPreferences("preferences", Context.MODE_PRIVATE)
        preferences.edit().putBoolean(key, value).apply()
    }
}

