// MenuModel.kt
import android.os.Parcel
import android.os.Parcelable

data class MenuModel(var name: String, var description: String, var price: Double, var image: String, var likes: Int) : Parcelable {
    constructor(parcel: Parcel) : this(
        parcel.readString() ?: "",
        parcel.readString() ?: "",
        parcel.readDouble(),
        parcel.readString() ?: "",
        parcel.readInt()
    ) {
    }

    override fun writeToParcel(parcel: Parcel, flags: Int) {
        parcel.writeString(name)
        parcel.writeString(description)
        parcel.writeDouble(price)
        parcel.writeString(image)
        parcel.writeInt(likes)
    }

    override fun describeContents(): Int {
        return 0
    }
}

// MenuDatabase.kt
import android.content.Context
import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase

@Database(entities = [MenuModel::class], version = 1)
abstract class MenuDatabase : RoomDatabase() {
    abstract fun menuDao(): MenuDao
}

// MenuDao.kt
import androidx.room.Dao
import androidx.room.Insert
import androidx.room.Query

@Dao
interface MenuDao {
    @Insert
    suspend fun insertMenu(menu: MenuModel)

    @Query("SELECT * FROM menu")
    suspend fun getMenu(): List<MenuModel>
}
