companions.content:
interface DishRepository {
    suspend fun getDishes(): List<Dish>
    suspend fun addDish(dish: Dish)
    suspend fun updateDish(dish: Dish)
    suspend fun deleteDish(id: Int)
}

class DishFragment : Fragment() {
    private lateinit var binding: FragmentDishBinding
    private lateinit var repository: DishRepository

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        binding = FragmentDishBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        repository = DishRepositoryImpl()
        binding.btnAddDish.setOnClickListener {
            val name = binding.etName.text.toString()
            val price = binding.etPrice.text.toString().toDouble()
            val description = binding.etDescription.text.toString()
            val image = binding.ivImage.toString()
            repository.addDish(Dish(0, name, price, description, image))
        }
    }
}

class DishAdapter(private val repository: DishRepository) : RecyclerView.Adapter<DishAdapter.ViewHolder>() {
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        return ViewHolder(LayoutInflater.from(parent.context).inflate(R.layout.item_dish, parent, false))
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val dish = repository.getDishes()[position]
        holder.bind(dish)
    }

    override fun getItemCount(): Int {
        return repository.getDishes().size
    }

    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        fun bind(dish: Dish) {
            itemView.tvName.text = dish.name
            itemView.tvPrice.text = "¥${dish.price}"n            itemView.tvDescription.text = dish.description
            itemView.ivImage.setImageResource(R.drawable.image)
        }
    }
}
