// Menu.java
// 牛太郎餐厅菜品模型
public class Menu {
    private String id;
    private String name;
    private String introduction;
    private double price;
    private String image;
    private int likes;

    public Menu(String id, String name, String introduction, double price, String image) {
        this.id = id;
        this.name = name;
        this.introduction = introduction;
        this.price = price;
        this.image = image;
    }

    // getter and setter
}

// MainActivity.java
// 牛太郎餐厅主界面
import android.os.Bundle
import android.view.View
import android.widget.Button
import android.widget.EditText
import android.widget.ImageView
import android.widget.Toast

public class MainActivity : AppCompatActivity() {
    private lateinit var menu: Menu

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        val button = findViewById<Button>(R.id.button)
        button.setOnClickListener {
            // 上传照片
            val image = Intent(Intent.ACTION_PICK, MediaStore.Images.Media.EXTERNAL_CONTENT_URI)
            startActivityForResult(image, 1)
        }
    }

    override fun onActivityResult(requestCode: Int, resultCode: Int, data: Intent?) {
        super.onActivityResult(requestCode, resultCode, data)
        if (requestCode == 1 && resultCode == Activity.RESULT_OK && data != null) {
            val image = data.data
            // 编辑文字介绍
            val introduction = findViewById<EditText>(R.id.introduction)
            // 设定价格
            val price = findViewById<EditText>(R.id.price)
            // 评论点赞
            val likes = findViewById<EditText>(R.id.likes)
            // 上传菜品
            menu = Menu(image.toString(), introduction.text.toString(), price.text.toString(), likes.text.toString().toDouble(), image.toString())
            Toast.makeText(this, "菜品上传成功", Toast.LENGTH_SHORT).show()
        }
    }
}
