// MenuAdapter.kt
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView

class MenuAdapter(private val menuModels: List<MenuModel>) : RecyclerView.Adapter<MenuAdapter.ViewHolder>() {
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        return ViewHolder(LayoutInflater.from(parent.context).inflate(R.layout.menu_item, parent, false))
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val menuModel = menuModels[position]
        holder.nameTextView.text = menuModel.name
        holder.descriptionTextView.text = menuModel.description
        holder.priceTextView.text = "¥${menuModel.price}" + "
        holder.imageView.setImageResource(R.drawable.${menuModel.image})
    }

    override fun getItemCount(): Int {
        return menuModels.size
    }

    class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val nameTextView: TextView = itemView.findViewById(R.id.name_text_view)
        val descriptionTextView: TextView = itemView.findViewById(R.id.description_text_view)
        val priceTextView: TextView = itemView.findViewById(R.id.price_text_view)
        val imageView: ImageView = itemView.findViewById(R.id.image_view)
    }
}
