class MenuAdapter(private val context: Context, private val menus: List<Menu>) : RecyclerView.Adapter<MenuAdapter.ViewHolder>() {
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        return ViewHolder(LayoutInflater.from(context).inflate(R.layout.item_menu, parent, false))
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val menu = menus[position]
        holder.name.text = menu.name
        holder.description.text = menu.description
        holder.price.text = "¥${menu.price}"

class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
    val name: TextView = itemView.findViewById(R.id.name)
    val description: TextView = itemView.findViewById(R.id.description)
    val price: TextView = itemView.findViewById(R.id.price)
    val image: ImageView = itemView.findViewById(R.id.image)
}

