class MenuAdapter(private val context: Context, private val menuList: ArrayList<Menu>) : RecyclerView.Adapter<MenuAdapter.ViewHolder>() {
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        return ViewHolder(LayoutInflater.from(context).inflate(R.layout.menu_item, parent, false))
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val menu = menuList[position]
        holder.nameTextView.text = menu.name
        holder.descriptionTextView.text = menu.description
        holder.priceTextView.text = "¥${menu.price}" // TODO: 支持多种货币
        holder.imageView.setImageResource(R.drawable.menu_image)
    }

    class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val nameTextView: TextView = itemView.findViewById(R.id.name_text_view)
        val descriptionTextView: TextView = itemView.findViewById(R.id.description_text_view)
        val priceTextView: TextView = itemView.findViewById(R.id.price_text_view)
        val imageView: ImageView = itemView.findViewById(R.id.image_view)
    }
}

