    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val view = inflater.inflate(R.layout.fragment_menu, container, false)
        recyclerView = view.findViewById(R.id.recycler_view)
        menuAdapter = MenuAdapter(this.context, ArrayList())
        recyclerView.adapter = menuAdapter
        return view
    }
}

class CommentAdapter(private val context: Context, private val commentList: ArrayList<Comment>) : RecyclerView.Adapter<CommentAdapter.ViewHolder>() {
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        return ViewHolder(LayoutInflater.from(context).inflate(R.layout.comment_item, parent, false))
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val comment = commentList[position]
        holder.contentTextView.text = comment.content
        holder.likesTextView.text = "${comment.likes} likes"
    }

    class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val contentTextView: TextView = itemView.findViewById(R.id.content_text_view)
        val likesTextView: TextView = itemView.findViewById(R.id.likes_text_view)
    }
}

