import android.content.Context
import android.util.Log
import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import androidx.lifecycle.ViewModel
import com.example.牛太郎餐厅.model.Menu

class MenuRepository(private val context: Context) : ViewModel() {
    private val menuList = MutableLiveData<List<Menu>>()

    fun getMenus(): LiveData<List<Menu>> {
        return menuList
    }

    fun addMenu(menu: Menu) {
        menuList.value = menuList.value?.plus(menu)
    }
}

import androidx.room.Dao
