import android.content.Context
import android.util.Log
import androidx.lifecycle.LiveData
import androidx.lifecycle.MutableLiveData
import com.example.牛太郎餐厅.model.Menu

class MenuService(private val context: Context) {
    private val menuRepository = MenuRepository(context)

    fun getMenus(): LiveData<List<Menu>> {
        return menuRepository.getMenus()
    }

    fun addMenu(menu: Menu) {
        menuRepository.addMenu(menu)
    }
}

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.example.牛太郎餐厅.model.Menu

class MenuAdapter(private val menuList: List<Menu>) : RecyclerView.Adapter<MenuAdapter.ViewHolder>() {
    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        return ViewHolder(LayoutInflater.from(parent.context).inflate(R.layout.menu_item, parent, false))
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        val menu = menuList[position]
        holder.nameTextView.text = menu.name
        holder.descriptionTextView.text = menu.description
        holder.priceTextView.text = "¥${menu.price}"n        Glide.with(holder.itemView.context).load(menu.photo).into(holder.photoImageView)
    }

    override fun getItemCount(): Int {
        return menuList.size
    }

    class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val nameTextView: TextView = itemView.findViewById(R.id.name_text_view)
        val descriptionTextView: TextView = itemView.findViewById(R.id.description_text_view)
        val priceTextView: TextView = itemView.findViewById(R.id.price_text_view)
        val photoImageView: ImageView = itemView.findViewById(R.id.photo_image_view)
    }
}

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.牛太郎餐厅.adapter.MenuAdapter
import com.example.牛太郎餐厅.model.Menu

class MenuFragment : Fragment() {
    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?): View? {
        val view = inflater.inflate(R.layout.fragment_menu, container, false)

        val recyclerView = view.findViewById<RecyclerView>(R.id.recycler_view)
        recyclerView.layoutManager = LinearLayoutManager(context)
        recyclerView.adapter = MenuAdapter(getMenus())

        val addButton = view.findViewById<Button>(R.id.add_button)
        addButton.setOnClickListener {
            val intent = Intent(context, UploadActivity::class.java)
            startActivity(intent)
        }

        return view
    }

    private fun getMenus(): List<Menu> {
        return MenuService(context).getMenus().value ?: emptyList()
    }
}

