import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.Toast;
import androidx.appcompat.app.AppCompatActivity;
import com.bumptech.glide.Glide;
import com.example.nittaro.menu.R;

public class MenuActivity extends AppCompatActivity {
    private ArrayList<Menu> menuList;
    private MenuAdapter menuAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_menu);

        Button addButton = findViewById(R.id.add_button);
        addButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                EditText menuName = findViewById(R.id.menu_name);
                EditText menuPrice = findViewById(R.id.menu_price);
                ImageView menuImage = findViewById(R.id.menu_image);

                Menu menu = new Menu(menuName.getText().toString(), menuPrice.getText().toString(), Glide.with(MenuActivity.this).load(menuImage.getDrawable()).into(menuImage));
                menuList.add(menu);
                menuAdapter.notifyDataSetChanged();
            }
        });
    }
}

